targets.query:
from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["service_name"] == "${service}")
  |> filter(fn: (r) => r._measurement == "process_cpu_usage")
  |> pivot(rowKey:["_time"], columnKey: ["_measurement"], valueColumn: "_value")
  |> map(fn: (r) => ({
    _value:  r.process_cpu_usage * 100.0
  }))
  |> last()

import "strings"

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["service_name"] == "${service}")
  |> filter(fn: (r) => r["_measurement"] == "process_files_max" or r["_measurement"] == "process_files_open")
  |> map(fn: (r) => ({
    _time: r._time,
    value: r._value,
    _measurement: strings.split(v: r._measurement, t: "_")[2]
  }))

import "strings"

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["service_name"] == "${service}")
  |> filter(fn: (r) => r["_measurement"] == "jvm_classes_loaded" or r["_measurement"] == "jvm_classes_unloaded")
  |> map(fn: (r) => ({
    _time: r._time,
    value: r._value,
    _measurement: strings.split(v: r._measurement, t: "_")[2]
  }))

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["service_name"] == "${service}")
  |> filter(fn: (r) => r._measurement == "jetty_threads_config_max" or r._measurement == "jetty_threads_busy")
  |> pivot(rowKey:["_time"], columnKey: ["_measurement"], valueColumn: "_value")
  |> map(fn: (r) => ({
    _value: r.jetty_threads_busy / r.jetty_threads_config_max * 100.0
  }))
  |> last()
